class linFamily: 

    surname = "Lin"    # common attributes

    def __init__(self, firstname, gender):    # self attributes
        self.firstname=firstname
        self.gender=gender

    def __repr__(self): 
        return "I am {b} {a}. Gender: {c}".format(
          a = self.surname, b = self.firstname, c = self.gender
        )
